package com.syntax.class03;

public class SHorthandOperators {

	public static void main(String[] args) {
		
		// +,-,*,/,%(mod)
		
		int num=100;
		num=num+100;
		System.out.println(num);
		// TODO Auto-generated method stub

		num+=100; //this exact meas--> this num=num+100
		
		System.out.println(num);
		
		num-=20;
		
		System.out.println(num);
		
		num/=10;
		
		System.out.println(num);
		
		num%=3;
		
		System.out.println(num);
		
		
	}

}
