package com.syntax.class07;

public class WhileLoopTaskOtherWay {
//incrementing from 1 to 30 odd numbers only or even numbers
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int e=1;
		while(e<=30) {
			if (e%2==0) {
			System.out.print(e);
		}e++;

	}
System.out.println("The end");
}
}